INSERT INTO categories (`name`, `category_info`) VALUES ("Giseigo (擬声語)", "These are sounds that humans and animals make. Some of them may sound very similar to what you learned growing up, and maybe some sound even closer to what you hear than what you write in your language.");
INSERT INTO categories (`name`, `category_info`) VALUES ("Giongo (擬音語)", "These are also real sounds. They're the ones you see used in manga and anime. They're the sound of the wind moving through the trees, the door slamming shut, and the phone ringing. Basically, any sound you hear that isn't coming out of the mouth of a person or animal falls under this category.");
INSERT INTO categories (`name`, `category_info`) VALUES ("Gitaigo (擬態語)", "The first of our mimetic set, these words describe conditions and states.");
INSERT INTO categories (`name`, `category_info`) VALUES ("Giyougo (擬容語)", "These describe movements and motions, usually relating to walking or traveling from place to place.");
INSERT INTO categories (`name`, `category_info`) VALUES ("Gijougo (擬情語)", "These words describe feelings. These are also used heavily in manga.");
INSERT INTO onomatopoeias (`onomatopoeia`, `category_id`, `meaning`, `example`, `date_created`) VALUES ("wan wan (わんわん / ワンワン)", "1", "woof woof", "夜中に、隣の家からワンワンと犬の鳴き声が聞こえた. In the middle of the night, I heard a 'wan wan' bark coming from the neighbor's house", "2024-05-14");
INSERT INTO onomatopoeias (`onomatopoeia`, `category_id`, `meaning`, `example`, `date_created`) VALUES ("goro goro (ごろごろ / ゴロゴロ)", "2", "thunder rumbling", "外では雷がゴロゴロと鳴っている. Outside thunder is rumbling", "2024-05-13");
INSERT INTO onomatopoeias (`onomatopoeia`, `category_id`, `meaning`, `example`, `date_created`) VALUES ("mushi mushi (むしむし)", "3", "too much warmth, unpleasantly hot", "今日は蒸し暑い（むしあつい）ですね. It's humid today.", "2024-05-12");
INSERT INTO onomatopoeias (`onomatopoeia`, `category_id`, `meaning`, `example`, `date_created`) VALUES ("uro uro (うろうろ)", "4", "wandering aimlessly", "彼は道に迷って、駅の周りをうろうろしていた. He got lost and was wandering around the station.", "2024-05-15");
INSERT INTO onomatopoeias (`onomatopoeia`, `category_id`, `meaning`, `example`, `date_created`) VALUES ("waku waku (わくわく)", "5", "excited from anticipation, pleasure, or happiness", "明日の旅行が楽しみでわくわくしている. I'm excited about tomorrow's trip.", "2024-05-16");
INSERT INTO onomatopoeias (`onomatopoeia`, `category_id`, `meaning`, `example`, `date_created`) VALUES ("pera pera (ぺらぺら)", "1", "Being able to speak a foreign language fluently", "日本語ぺらぺらになりたい。 I want to be fluent in Japanese", "2024-06-04");
INSERT INTO onomatopoeias (`onomatopoeia`, `category_id`, `meaning`, `example`, `date_created`) VALUES ("zaa zaa (ざーざー / ザーザー)", "2", "Lots of heavy rain pouring down", "ザーザー降っているから、傘を持って行った方がいいよ。 It's pouring with rain so you'd better take an umbrella.", "2024-06-04");
INSERT INTO onomatopoeias (`onomatopoeia`, `category_id`, `meaning`, `example`, `date_created`) VALUES ("buru buru (ぶるぶる)", "4", "Trembling from cold, fear, or anger", "子犬が寒くてぶるぶる震えています。 The puppy is shaking/trembling from the cold", "2024-06-04");
INSERT INTO onomatopoeias (`onomatopoeia`, `category_id`, `meaning`, `example`, `date_created`) VALUES ("moya moya (もやもや)", "5", "Worrying or wondering what to do", "心がもやもやしている。My mind is feeling hazy/foggy", "2024-06-04");
